mapper.select:
select  * from question
where username=#{username}
order by question_id desc

mapper.update:
update question
set question_view=question_view+1
where question_id=#{questionId}

update question
set comment_count = comment_count+1
where question_id=#{questionId}

update question
set comment_count = comment_count-1
where question_id=#{questionId}
